bool isCoprime(int a, int b) 
{ 
    // Everything divides 0 
    if (a == 0 || b == 0) 
        return false; 

    // base case 
    if (a == 1 || b == 1) 
        return true; 

    // a is divisible by b 
    if (a % b == 0 || b % a == 0) 
        return false; 

    // a is even 
    if (a % 2 == 0)  
        return (b % 2 == 1) ? isCoprime(a/2, b) :  
                              isCoprime(a/2, b/2); 

    // b is even 
    if (b % 2 == 0) 
        return isCoprime(a, b/2); 

    // reduce larger number 
    return (a > b) ? isCoprime(a-b, b) : 
                       isCoprime(a, b-a); 
}

