def get_longest_increasing_subsequence(input):
    '''This algorithm will find the longest increasing subsequence in an array.'''
    n = len(input)
    leng = [1]*n
    for i in range(n):
        for j in range(i):
            if(input[i] > input[j] and leng[i] < leng[j] + 1):
                leng[i] = leng[j]+1
    max_seq_len = max(leng)
    max_seq = []
    for i in range(n):
        if(leng[i] == max_seq_len):
            max_seq.append(input[i])
            max_seq_len -= 1
    max_seq.reverse()
    return max_seq

