function evaluateExpression(str) {
    let expression = str.split(' ').map(el => {
        if (el === '+' || el === '-' || el === '*' || el === '/') {
            return el;
        }
        return parseInt(el);
    });
    let stack = [];
    let result;

    for (let i = 0; i < expression.length; i++) {
        let el = expression[i];
        if (typeof el === 'number') {
            stack.push(el);
        } else {
            let secondOperand = stack.pop();
            let firstOperand = stack.pop();
            switch (el) {
                case '+':
                    result = firstOperand + secondOperand;
                    break;
                case '-':
                    result = firstOperand - secondOperand;
                    break;
                case '*':
                    result = firstOperand * secondOperand;
                    break;
                case '/':
                    result = firstOperand / secondOperand;
                    break;
            }
            stack.push(result);
        }
    }
    return stack.pop();
}

