  // Initialize variables
  let left = 0;
  let right = array.length - 1;

  // Iterate while left <= right 
  while (left <= right) {
    // Get middle index
    let mid = left + Math.floor((right - left) / 2);

    // Match found
    if (array[mid] === target) {
      return mid;
    }

    // If target is smaller than the array element at mid
    if (target < array[mid]) {
      right = mid - 1;
    }
