    public static Scanner scan;

    public static void main(String[] args) {
        scan = new Scanner(System.in);
        playerX = true;
        initializeBoard();

        while(true) {
            printBoard();
            nextMove(playerX);
            if(checkWinner()) {
                break;
            }
            playerX = !playerX;
        }

