def evaluatePostfix(exp): 

	# Create a stack 
	stack = [] 

	# Scan all characters one by one 
	for i in exp: 

		# If the character is a number, push it to the stack 
		if i.isdigit(): 
			stack.append(int(i)) 

		# If the character is an operator, pop two 
		# elements from stack apply the operator 
