def quicksort(arr):
    """This function takes an array as parameter and sorts it in-place"""
    if len(arr) <= 1:
        return
    pivot = arr[0]
    left, right = [], []
    for i in range(1, len(arr)):
        if arr[i] < pivot:
            left.append(arr[i])
        else:
            right.append(arr[i])
    quicksort(left)
    quicksort(right)
    arr[:] = left + [pivot] + right

