string longestCommonSubstring(string s1, string s2) { 
    int s1Length = s1.size(); 
    int s2Length = s2.size(); 

    int** dp = new int*[s1Length + 1]; 
    for (int i = 0; i <= s1Length; ++i) { 
        dp[i] = new int[s2Length + 1]; 
        dp[i][0] = 0; 
    } 
    for (int j = 0; j <= s2Length; ++j) { 
        dp[0][j] = 0; 
    } 

    int maxLength = 0; 
    int end; 
    for (int i = 1; i <= s1Length; ++i) { 
        for (int j = 1; j <= s2Length; ++j) { 
            if (s1[i - 1] == s2[j - 1]) { 
                dp[i][j] = 1 + dp[i - 1][j - 1]; 
                if (maxLength < dp[i][j]) { 
                    maxLength = dp[i][j]; 
                    end = i; 
                } 
            } 
            else { 
                dp[i][j] = 0; 
            } 
        } 
    } 
    return s1.substr(end - maxLength, maxLength); 
}

