int compareToFindSmallest(int[] numbers, int targetIndex) {

    int smallestIndex = 0;
    for (int i = 1; i < numbers.length; i++) 
    {
        if (numbers[i] < numbers[smallestIndex]) {
            smallestIndex = i;
        }
    }

    int tempIndex = smallestIndex;
    int resultIndex = -1;
    int count = 0;
    while (resultIndex == -1) {
        count++;
        if (smallestIndex == tempIndex) {
            if (count == targetIndex) {
                resultIndex = tempIndex;
            }
            tempIndex++;
        } else {
            if (numbers[tempIndex] == numbers[smallestIndex]) {
                if (count == targetIndex) {
                    resultIndex = tempIndex;
                }
                tempIndex++;
            } else {
                smallestIndex = tempIndex;
            }
        }

        if (tempIndex >= numbers.length) {
            tempIndex = smallestIndex + 1;
        }
    }

    return resultIndex;
}

