def postfix_to_infix(expression):
    stack = []
    for c in expression.split():
        if c in "+-*/":
            operand1 = stack.pop()
            operand2 = stack.pop()
            stack.append("({} {} {})".format(operand2, c, operand1))
        else:
            stack.append(c)
    return stack[0]

