def sort_words_by_frequency(words):
    frequency_dict = {}
    for word in words:
        if word in frequency_dict:
            frequency_dict[word] += 1
        else:
            frequency_dict[word] = 1

    sorted_words = sorted(frequency_dict.items(), key=lambda x: x[1], reverse=True)
