    int i, j, min_idx; 

    // One by one move boundary of unsorted sub-array 
    for (i = 0; i < n-1; i++) {
        // Find the minimum element in unsorted array 
        min_idx = i; 
        for (j = i+1; j < n; j++) {
            if (nums[j] < nums[min_idx]) 
            min_idx = j; 
        }

        // Swap the found minimum element with the first element 
        swap(&nums[min_idx], &nums[i]); 
    } 
}

