let binarySearch = (array, x) => {
  let start = 0;
  let end = array.length - 1;

  while (start <= end) {
    let middle = Math.floor((start + end) / 2);
    if (array[middle] === x) {
      return middle;
    } else if (x < array[middle]) {
      end = middle - 1;
    } else {
      start = middle + 1;
    }  
  }
  return -1;
}

