def rot13(string):
    encoded = ""
    for char in string:
        if char.isalpha():
            ascii_val = ord(char)
            ascii_val += 13
            if ascii_val > ord('z'):
                ascii_val -= 26
            encoded += chr(ascii_val)
        else:
            encoded += char
    return encoded

